import { Card, CardContent, Typography, makeStyles } from '@material-ui/core';

import React from 'react'

const useStyles = makeStyles({
    wrapper: (props) => {
        if (props.type === 'confirmed')
            return { borderLeft: '5px solid #c9302c' }
        else if (props.type === 'recovered')
            return { borderLeft: '5px solid #c9302c' }
        else
            return { borderLeft: '5px solid #c9302c' }
    }
})

const HighlightCard = ({ title, count, type }) => {
    return (
        <Card>
            <CardContent>
                <Typography component="p" variant="body2">{title}</Typography>
                <Typography component="span" variant="body2">{count}</Typography>
            </CardContent>
        </Card>
    )
}

export default HighlightCard
